    declare_target_table %Q{select * from dbo.ViewCRReporting where dbo.ViewCRReporting .ChangeRequestID in (select FactChangeRequest.ChangeRequestID  from dbo.FactChangeRequest where FactChangeRequest .MappingSchemaID in (Select MappingSchemaID from az.MappingSchema where ParentMappingSchema='2'))},'target'

    #define sourceissueid field's mapping relationship
    mp target.sourceissueid do
        "#{source.internal_system_id}/#{source.system_type.upcase}"
    end

    #define state field's mapping relationship
    m target.state do
    	if source.state.gsub(/\s|"/, '').downcase=='Lab Review'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'New'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Support Review'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    	    'New'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Development'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'Being Fixed'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Awaiting Decision'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'Investigating'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Testing'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'Validating Fix'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Awaiting Release'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'Fix Generally Available/ Fix Validated'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Forwarded'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'New'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Deferred'.gsub(/\s/, '').downcase && (source.disposition_classification.gsub(/\s|"/, '').downcase!='Under Investigation'.gsub(/\s/, '').downcase || source.disposition_classification.gsub(/\s|"/, '').downcase!='Fixed'.gsub(/\s/, '').downcase)
    		'Deferred'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Duplicate'.gsub(/\s/, '').downcase
    		'Duplicate'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Completed'.gsub(/\s/, '').downcase
    		'Closed'
    	elsif source.state.gsub(/\s|"/, '').downcase=='Closed-No Change'.gsub(/\s/, '').downcase
    		'Closed no Change'
    	elsif source.disposition_classification.gsub(/\s|"/, '').downcase=='Under Investigation'.gsub(/\s/, '').downcase && (source.state.gsub(/\s|"/, '').downcase!='Duplicate'.gsub(/\s/, '').downcase && source.state.gsub(/\s|"/, '').downcase!='Completed'.gsub(/\s/, '').downcase && source.state.gsub(/\s|"/, '').downcase!='Closed-No Change'.gsub(/\s/, '').downcase)
    		'Investigating'
    	elsif source.disposition_classification.gsub(/\s|"/, '').downcase=='Fixed'.gsub(/\s/, '').downcase && (source.state.gsub(/\s|"/, '').downcase!='Duplicate'.gsub(/\s/, '').downcase && source.state.gsub(/\s|"/, '').downcase!='Completed'.gsub(/\s/, '').downcase && source.state.gsub(/\s|"/, '').downcase!='Closed-No Change'.gsub(/\s/, '').downcase)
    		'Fix Integrated'
        else 
            if source.state.nil? && source.DISPOSITION_CLASSIFICATION.nil?
                ''
            elsif !source.state.nil? && source.DISPOSITION_CLASSIFICATION.nil?
                source.state
            elsif source.state.nil? && !source.DISPOSITION_CLASSIFICATION.nil?
                source.DISPOSITION_CLASSIFICATION
            else
                "#{source.state}/#{source.DISPOSITION_CLASSIFICATION}"
            end
    	end    		
    end 

    #define foundinstep field's mapping relationship
    m target.foundinstep do
