    	elsif source.urgency.gsub(/\s|"/, '').downcase=='3=High: Frequent Problem' .gsub(/\s/, '').downcase
    		'Very Likely'
    	elsif source.urgency.gsub(/\s|"/, '').downcase=='2=Medium: Occurs Occasionally'.gsub(/\s/, '').downcase
    		'Likely'
    	elsif source.urgency.gsub(/\s|"/, '').downcase=='1=Low: Problem Occurs Rarely'.gsub(/\s/, '').downcase
    		'Unlikely'
        elsif source.urgency.nil?
            ''
    	else
    		 source.urgency
    	end
    end

    #define showstopper field's mapping relationship
    m target.showstopper do
    	if source.show_stopper.gsub(/\s|"/, '').downcase=='N'.gsub(/\s/, '').downcase
    		'N'
    	elsif source.show_stopper.gsub(/\s|"/, '').downcase=='Y'.gsub(/\s/, '').downcase
    		'Y'
        elsif source.show_stopper.nil?
            ' '
    	else
    		 source.show_stopper
    	end
    end

    #define enginneringpriority field's mapping relationship
    m target.enginneringpriority do
    	if source.engineering_priority.gsub(/\s|"/, '').downcase=='High'.gsub(/\s/, '').downcase
