		elsif source.engineering_priority.gsub(/\s|"/, '').downcase=='Low'.gsub(/\s/, '').downcase
		    'Low'
		elsif source.engineering_priority.gsub(/\s|"/, '').downcase=='Medium'.gsub(/\s/, '').downcase
			'Medium'
        elsif source.engineering_priority.nil?
            ''
